import requests
import os
import csv
from bs4 import BeautifulSoup


def write(lines):
    with open(file='Toyota_Models.csv', encoding='utf-8', mode='a', newline='') as csv_file:
        writer = csv.writer(csv_file, delimiter=',')
        writer.writerows(lines)


def main():
    url = 'https://www.toyota.com/'
    soup = BeautifulSoup(html, 'html5lib')
    cards = soup.find_all(attrs={'class': 'vehicle-wrapper'})
    for card in cards:
        name = card.find(attrs={'class': 'model'}).text.strip()
        if card.select('.vehicle-image-wrapper img')[1].has_attr('srcset'):
            image = card.select('.vehicle-image-wrapper img')[1]['srcset']
        elif card.select('.vehicle-image-wrapper img')[1].has_attr('data-srcset'):
            image = card.select('.vehicle-image-wrapper img')[1]['data-srcset']
        line = [name, 'https://www.toyota.com' + image]
        print(line)
        write(lines=[line])


